sysauto_script.script:
//Sample script to show nightly attachment cleanup
//This is looking for data older than 7 days ... 
var count = 0;
var gr = new GlideRecord("sys_attachment");
gr.addQuery("table_name=x_snc_hotel_author_content_review^sys_created_on<=javascript:gs.beginningOfLast7Days()^ORDERBYDESCsys_updated_on");
gr.query();
while(gr.next()) {
	count++;
	gs.info('File Name: ' + gr.file_name + ' - Content Type: ' + gr.content_type + ' - Created: ' + gr.sys_created_on);
}
gs.info('record count = ' + count);
